import React, { useState } from 'react';

import './newpermit.css';
import { buildApiUrl } from '../config/api';

const businessTypes = [
  'COOPERATIVE',
  'CORPORATION',
  'ONE PERSON CORPORATION',
  'PARTNERSHIP',
  'SOLE PROPRIETORSHIP',
];

const regions = ['NCR', 'Region I', 'Region II']; // Placeholder
const provinces = ['Metro Manila', 'Ilocos Norte']; // Placeholder
const cities = ['Quezon City', 'Laoag City']; // Placeholder
const barangays = ['Barangay 1', 'Barangay 2']; // Placeholder

// const requirementsList = [
//   'Proof of Registration (DTI, SEC, CDA, etc.)',
//   'Proof of right to use location (Tax Declaration, Lease/MOA, or consent)',
//   'Location Plan or sketch',
//   'Barangay Clearance',
//   'Market Clearance (if stallholder)',
//   'Photocopy of Occupancy Permit (if newly constructed building)',
//   'Community Tax Certificate (CEDULA)',
//   'Actual Photo of Business Establishment (Interior)',
//   'Actual Photo of Business Establishment (Exterior)',
// ];

const NewPermit = () => {
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({
    businessType: '',
    registrationNo: '',
    businessName: '',
    tin: '',
    tradeName: '',
    firstName: '',
    middleName: '',
    lastName: '',
    extensionName: '',
    sex: '',
    mailAddress: '',
    telephone: '',
    mobile: '',
    region: '',
    province: '',
    city: '',
    barangay: '',
    addressLine1: '',
    zipcode: '',
    requirements: [],
    businessLineFiles: null,
  });
  const [uploadedFiles, setUploadedFiles] = useState([]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      setForm((prev) => ({ ...prev, [name]: checked }));
    } else if (type === 'file') {
      setUploadedFiles(Array.from(files));
    } else {
      setForm((prev) => ({ ...prev, [name]: value }));
    }
  };
  const next = () => setStep((s) => s + 1);
  const prev = () => setStep((s) => s - 1);

  // Submit form to backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Prepare permit data
      const permitData = {
        business_type: form.businessType,
        registration_number: form.registrationNo || '',
        business_name: form.businessName,
        tax_identification_number: form.tin || '',
        trade_name: form.tradeName || '',
        owner_first_name: form.firstName || '',
        owner_middle_name: form.middleName || '',
        owner_last_name: form.lastName || '',
        owner_extension_name: form.extensionName || '',
        owner_sex: form.sex || '',
        mail_address: form.mailAddress || '',
        telephone: form.telephone || '',
        mobile: form.mobile || '',
        region_id: 1, // Default to NCR for now
        province_id: 1, // Default to Metro Manila for now
        city_id: 1, // Default to Quezon City for now
        barangay_id: 1, // Default to Barangay 1 for now
        address_line1: form.addressLine1 || '',
        zipcode: form.zipcode || '',
      };

      // Submit permit
      const permitResponse = await fetch(buildApiUrl('/permits'), {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(permitData)
      });

      if (!permitResponse.ok) {
        const errorData = await permitResponse.json();
        throw new Error(errorData.error || 'Failed to create permit');
      }

      alert('Permit submitted successfully!');
      // Reset form or redirect
      setForm({
        businessType: '',
        registrationNo: '',
        businessName: '',
        tin: '',
        tradeName: '',
        firstName: '',
        middleName: '',
        lastName: '',
        extensionName: '',
        sex: '',
        mailAddress: '',
        telephone: '',
        mobile: '',
        region: '',
        province: '',
        city: '',
        barangay: '',
        addressLine1: '',
        zipcode: '',
        requirements: [],
        businessLineFiles: null,
      });
      setStep(1);

    } catch (error) {
      console.error('Submit error:', error);
      alert(`Error submitting permit: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="permit-form-container">
      {/* Step 1: Business Info & Contact */}
      {step === 1 && (
        <form>
          <div className="permit-section-title">*Business Information Registration</div>
          <div className="permit-form-row">
            <div>
              <label>Business Type <span style={{ color: 'red' }}>*</span></label>
              <select name="businessType" value={form.businessType} onChange={handleChange} required>
                <option value="">Select Type</option>
                {businessTypes.map((type) => <option key={type} value={type}>{type}</option>)}
              </select>
            </div>
            <div>
              <label>DTI/SEC/CDA Registration No.</label>
              <input name="registrationNo" value={form.registrationNo || ''} onChange={handleChange} autoComplete="off" />
            </div>
          </div>
          <div className="permit-form-row">
            <div>
              <label>Business Name <span style={{ color: 'red' }}>*</span></label>
              <input name="businessName" value={form.businessName} onChange={handleChange} required autoComplete="organization" />
            </div>
            <div>
              <label>Tax Identification No.</label>
              <input name="tin" value={form.tin || ''} onChange={handleChange} autoComplete="off" />
            </div>
          </div>
          <div className="permit-form-row">
            <div>
              <label>Trade Name/Franchise (if applicable)</label>
              <input name="tradeName" value={form.tradeName || ''} onChange={handleChange} autoComplete="off" />
            </div>
            <div>
              <label>First Name</label>
              <input name="firstName" value={form.firstName || ''} onChange={handleChange} autoComplete="given-name" />
            </div>
            <div>
              <label>Middle Name</label>
              <input name="middleName" value={form.middleName || ''} onChange={handleChange} autoComplete="additional-name" />
            </div>
            <div>
              <label>Last Name</label>
              <input name="lastName" value={form.lastName || ''} onChange={handleChange} autoComplete="family-name" />
            </div>
            <div>
              <label>Extension Name</label>
              <input name="extensionName" value={form.extensionName || ''} onChange={handleChange} autoComplete="off" />
            </div>
            <div>
              <label>Sex</label>
              <select name="sex" value={form.sex || ''} onChange={handleChange}>
                <option value="">Select</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
              </select>
            </div>
          </div>
          <div className="permit-section-title">*CONTACT INFORMATION</div>
          <div className="permit-form-row">
            <div>
              <label>Mail Address</label>
              <input name="mailAddress" value={form.mailAddress || ''} onChange={handleChange} autoComplete="street-address" />
            </div>
            <div>
              <label>Telephone Number</label>
              <input name="telephone" value={form.telephone || ''} onChange={handleChange} autoComplete="tel" />
            </div>
            <div>
              <label>Mobile Number</label>
              <input name="mobile" value={form.mobile || ''} onChange={handleChange} autoComplete="tel" />
            </div>
          </div>
          <div style={{ marginTop: 16 }}>
            <button type="button" onClick={next}>NEXT</button>
            <button type="button" style={{ marginLeft: 8 }}>CANCEL</button>
          </div>
        </form>
      )}
      {/* Step 2: Business Address */}
      {step === 2 && (
        <form>
          <div className="permit-section-title">*Business Address</div>
          <div className="permit-form-row">
            <div>
              <label>Region</label>
              <select name="region" value={form.region} onChange={handleChange}>
                <option value="">Select Region</option>
                {regions.map((r) => <option key={r} value={r}>{r}</option>)}
              </select>
            </div>
            <div>
              <label>Province</label>
              <select name="province" value={form.province} onChange={handleChange}>
                <option value="">Select Province</option>
                {provinces.map((p) => <option key={p} value={p}>{p}</option>)}
              </select>
            </div>
            <div>
              <label>City/Municipality</label>
              <select name="city" value={form.city} onChange={handleChange}>
                <option value="">Select City/Municipality</option>
                {cities.map((c) => <option key={c} value={c}>{c}</option>)}
              </select>
            </div>
            <div>
              <label>Barangay</label>
              <select name="barangay" value={form.barangay} onChange={handleChange}>
                <option value="">Select Barangay</option>
                {barangays.map((b) => <option key={b} value={b}>{b}</option>)}
              </select>
            </div>
          </div>
          <div className="permit-form-row">
            <div>
              <label>Address line 1</label>
              <input name="addressLine1" value={form.addressLine1 || ''} onChange={handleChange} autoComplete="street-address" />
            </div>
            <div>
              <label>Zipcode</label>
              <input name="zipcode" value={form.zipcode || ''} onChange={handleChange} autoComplete="postal-code" />
            </div>
          </div>
          <div className="permit-form-row">
            <div style={{ width: '100%' }}>
              {/* Google Maps iframe removed as requested */}
            </div>
          </div>
          <div style={{ marginTop: 16 }}>
            <button type="button" onClick={next}>NEXT</button>
            <button type="button" onClick={prev} style={{ marginLeft: 8 }}>CANCEL</button>
          </div>
        </form>
      )}
      {/* Step 3: Business Requirements */}
      {step === 3 && (
        <form onSubmit={handleSubmit}>
          <div className="permit-section-title">*Business Requirements</div>
          <div style={{ marginBottom: 12, fontWeight: 'bold' }}>*Upload Your Requirements</div>
          <div style={{ marginBottom: 12 }}>(Please upload necessary requirements)</div>
          <ul style={{ marginBottom: 16 }}>
            {requirementsList.map((req, idx) => <li key={idx}>{req}</li>)}
          </ul>
          <div className="permit-form-row">
            <div>
              <label>CHOOSE FILE</label>
              <input type="file" multiple onChange={handleChange} />
            </div>
          </div>
          <div style={{ margin: '1rem 0' }}>
            {uploadedFiles.length > 0 && (
              <ul>
                {uploadedFiles.map((file, idx) => (
                  <li key={idx}>{file.name}</li>
                ))}
              </ul>
            )}
          </div>
          <div style={{ marginTop: 16 }}>
            <button type="submit" disabled={loading}>{loading ? 'Submitting...' : 'Submit'}</button>
            <button type="button" onClick={prev} style={{ marginLeft: 8 }}>Previous</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default NewPermit;
