import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [activeModal, setActiveModal] = useState(null);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const openModal = (modalId) => {
    setActiveModal(modalId);
  };

  const closeModal = () => {
    setActiveModal(null);
  };

  const dashboardBoxes = [
    { id: 'box1', title: 'Business Permits', description: 'Manage and track business permit applications' },
    { id: 'box2', title: 'Renewals', description: 'Handle permit renewals and extensions' },
    { id: 'box3', title: 'Requirements', description: 'View and manage permit requirements' },
    { id: 'box4', title: 'Reports', description: 'Generate and view business reports' },
    { id: 'box5', title: 'Operation Time', description: 'View business operation hours and schedule' }
  ];

  return (
    <div className="dashboard-layout">
      <aside className={`sidebar ${isSidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}>
        <div className="sidebar-header">
          <button className="sidebar-toggle" onClick={toggleSidebar}>
            <span className={`hamburger ${isSidebarOpen ? 'open' : ''}`}>
              <span></span>
              <span></span>
              <span></span>
            </span>
          </button>
        </div>
        <div className="sidebar-content">
          <button className="sidebar-btn" onClick={() => navigate('/newpermit')}>
            New Business Permit
          </button>
          <button className="sidebar-btn" onClick={() => navigate('/renewalpermit')}>
            Renewal Permit
          </button>
        </div>
      </aside>
      <main className="dashboard-container">
        <div className="dashboard-header">
          <button className="mobile-toggle" onClick={toggleSidebar}>
            <span className={`hamburger ${isSidebarOpen ? 'open' : ''}`}>
              <span></span>
              <span></span>
              <span></span>
            </span>
          </button>
        </div>
        <div className="dashboard-title">Welcome! </div>

        <div className="dashboard-grid">
          {dashboardBoxes.map((box) => (
            <div
              key={box.id}
              className="dashboard-box"
              onClick={() => openModal(box.id)}
            >
              <div className="box-content">
                <h3 className="box-title">{box.title}</h3>
                <p className="box-description">{box.description}</p>
                <div className="box-icon">üìã</div>
              </div>
            </div>
          ))}
        </div>

        {/* Modal Overlay */}
        {activeModal && (
          <div className="modal-overlay" onClick={closeModal}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <div className="modal-header">
                <h2 className="modal-title">
                  {dashboardBoxes.find(box => box.id === activeModal)?.title}
                </h2>
                <button className="modal-close" onClick={closeModal}>
                  <span className="close-icon">√ó</span>
                </button>
              </div>
              <div className="modal-body">
                {activeModal === 'box5' ? (
                  <div className="operation-time-info">
                    <h3 className="operation-time-title">Operation Time</h3>
                    <div className="operation-time-details">
                      <p className="operation-days">Monday to Friday</p>
                      <p className="operation-hours">8am to 5pm</p>
                    </div>
                  </div>
                ) : (
                  <>
                    <p className="modal-text">Sample text</p>
                    <p className="modal-description">
                      This is a sample description for the {dashboardBoxes.find(box => box.id === activeModal)?.title.toLowerCase()} section. 
                      Here you can add more detailed information about this feature.
                    </p>
                  </>
                )}
              </div>
              <div className="modal-footer">
                <button className="modal-back-btn" onClick={closeModal}>
                  ‚Üê Back
                </button>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
